mapper.update:
UPDATE verify
SET result = #{result}
WHERE vi_id = #{viId}

mapper.select:
SELECT COUNT(*)
FROM verify
WHERE vi_user_id = #{userId}
AND vi_ch_id = #{chId}
AND result = 'Y'

mapper.update:
UPDATE members
SET achieve_rate = #{achieveRate}
WHERE mem_user_id = #{userId}
AND mem_ch_id = #{chId}
