  const handleChange = (event) => {
    // `event.target`에서 `name`과 `value`를 추출
    const { name, value } = event.target;

    // formData 상태 업데이트
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Selected Gender:", formData.gender);
    // Submit handling logic here
  };

  return (
    <BasicLayout
      image={curved6}
      title="Select your gender"
      description="Please select your gender to continue."
    >
      <Card>
        <SoftBox
          component="form"
          role="form"
          onSubmit={handleSubmit}
          pt={2}
          pb={3}
          px={3}
        >
          <AppBar position="static">
            <Tabs
              value={tabValue}
              onChange={handleChange}
              indicatorColor="primary"
              textColor="primary"
              centered
            >
              <Tab label="남자" />
              <Tab label="여자" />
            </Tabs>
          </AppBar>
          <SoftBox p={3} textAlign="center">
            <p>
              You have selected: {formData.gender === "male" ? "남자" : "여자"}
            </p>
          </SoftBox>
        </SoftBox>
      </Card>
    </BasicLayout>
  );
}

