import React, { useState } from "react";
import { Card, AppBar, Tabs, Tab } from "@mui/material";
import { useNavigate } from "react-router-dom";
import SoftBox from "components/SoftBox";
import BasicLayout from "layouts/authentication/components/BasicLayout";
import backimage from "assets/images/youn/digdas.jpg";
import SoftTypography from "components/SoftTypography";

function Survey() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    gender: "female",
    age: "young",
    goal: "Overall health improvement",
    level: "Intermediate",
    abnormal: "no health problems",
  });

  const [planData, setPlanData] = useState({
    planName: "",
    planDesc: "",
  });

  // Tab 저장
  const [genderTabValue, setGenderTabValue] = useState(0);
  const [ageTabValue, setAgeTabValue] = useState(0);
  const [goalTabValue, setGoalTabValue] = useState(0);
  const [levelTabValue, setLevelTabValue] = useState(0);
  const [abnormalTabValue, setAbnormalTabValue] = useState(0);

  // 성별
  const handleGenderTabChange = (event, newValue) => {
    const newGender = newValue === 0 ? "male" : "female";
    setGenderTabValue(newValue);
    setFormData({ ...formData, gender: newGender });
  };

  // 나이
  const handleAgeTabChange = (event, newValue) => {
    const newAge = newValue === 0 ? "young" : "old";
    setAgeTabValue(newValue);
    setFormData({ ...formData, age: newAge });
  };

  // 운동목표
  const handleGoalTabChange = (event, newValue) => {
    // 목표에 따른 레이블을 객체로 매핑
    const goalOptions = {
      0: "Body fat reduction",
      1: "Muscle gain",
      2: "Overall health improvement",
    };
    // newValue를 사용하여 해당 목표를 찾음
    const newGoal = goalOptions[newValue];
    // 상태 업데이트
    setGoalTabValue(newValue); // 이 변수의 이름이 목적에 맞게 설정되었는지 확인하세요
    setFormData((prev) => ({
      ...prev,
      goal: newGoal,
    }));
  };

  // 운동수준
  const handleLevelTabChange = (event, newValue) => {
    // 목표에 따른 레이블을 객체로 매핑
    const levelOptions = {
      0: "beginner",
      1: "Intermediate",
      2: "advanced",
    };
    // newValue를 사용하여 해당 목표를 찾음
    const newLevel = levelOptions[newValue];
    // 상태 업데이트
    setLevelTabValue(newValue); // 이 변수의 이름이 목적에 맞게 설정되었는지 확인하세요
    setFormData((prev) => ({
      ...prev,
      level: newLevel,
    }));
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  // 건강이상
  // const handleAbnormalTabChange = (event, newValue) => {
  //   const newAge = newValue === 0 ? "young" : "old";
  //   setAgeTabValue(newValue);
  //   setFormData({ ...formData, age: newAge });
  // };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Selected Gender:", formData.gender);
    // Submit handling logic here
  };

  return (
    <BasicLayout
      image={backimage}
      title="Select your gender"
      description="Please select your gender to continue."
    >
      <Card>
        <SoftBox
          component="form"
          role="form"
          onSubmit={handleSubmit}
          pt={2}
          pb={3}
          px={3}
        >
          <AppBar position="static">
            <SoftBox p={3} mb={1} textAlign="center">
              <SoftTypography variant="h5" fontWeight="medium">
                1. 당신의 성별은?
              </SoftTypography>
            </SoftBox>
            <Tabs
              value={genderTabValue}
              onChange={handleGenderTabChange}
              indicatorColor="primary"
              textColor="primary"
              centered
            >
              <Tab label="남자" />
              <Tab label="여자" />
            </Tabs>
            <SoftBox p={3} mb={1} textAlign="center">
              <SoftTypography variant="h5" fontWeight="medium">
                2. 당신의 연령대는?
              </SoftTypography>
            </SoftBox>
            <Tabs
              value={ageTabValue}
              onChange={handleAgeTabChange}
              indicatorColor="primary"
              textColor="primary"
              centered
            >
              <Tab label="35세 이하" />
              <Tab label="36세 이상" />
            </Tabs>
            <SoftBox p={3} mb={1} textAlign="center">
              <SoftTypography variant="h5" fontWeight="medium">
                3. 당신의 운동 목표는?
              </SoftTypography>
            </SoftBox>
            <Tabs
              value={goalTabValue}
              onChange={handleGoalTabChange}
              indicatorColor="primary"
              textColor="primary"
              centered
            >
              <Tab label="체지방량 감소" />
              <Tab label="근육량 증가" />
              <Tab label="종합 건강" />
            </Tabs>
            <SoftBox p={3} mb={1} textAlign="center">
              <SoftTypography variant="h5" fontWeight="medium">
                4. 당신의 운동 수준은?
              </SoftTypography>
            </SoftBox>
            <Tabs
              value={levelTabValue}
              onChange={handleLevelTabChange}
              indicatorColor="primary"
              textColor="primary"
              centered
            >
              <Tab label="입문" />
              <Tab label="중급" />
              <Tab label="전문가" />
            </Tabs>
            <SoftBox p={3} mb={1} textAlign="center">
              <SoftTypography variant="h5" fontWeight="medium">
                5. 운동 시 주의가 필요한 건강 이상은?
              </SoftTypography>
            </SoftBox>
            <div className="grid-wrapper grid-col-auto">
              <label htmlFor="radio-card-9" className="radio-card">
                <input
                  type="Checkbox"
                  name="abnormal"
                  id="radio-card-9"
                  value="cardiovascular disease"
                  checked={formData.abnormal === "cardiovascular disease"}
                  onChange={handleChange}
                />
                <div className="card-content-wrapper">
                  <span className="check-icon"></span>
                  <div className="card-content">
                    <i className="fi fi-br-heart"></i>
                    <h4>심혈관계</h4>
                  </div>
                </div>
              </label>
              <label htmlFor="radio-card-10" className="radio-card">
                <input
                  type="radio"
                  name="abnormal"
                  id="radio-card-10"
                  value="musculoskeletal disorders"
                  checked={formData.abnormal === "musculoskeletal disorders"}
                  onChange={handleChange}
                />
                <div className="card-content-wrapper">
                  <span className="check-icon"></span>
                  <div className="card-content">
                    <i className="fi fi-br-bone"></i>
                    <h4>근골격계</h4>
                  </div>
                </div>
              </label>
              <label htmlFor="radio-card-11" className="radio-card">
                <input
                  type="radio"
                  name="abnormal"
                  id="radio-card-11"
                  value="respiratory diseases"
                  checked={formData.abnormal === "respiratory diseases"}
                  onChange={handleChange}
                />
                <div className="card-content-wrapper">
                  <span className="check-icon"></span>
                  <div className="card-content">
                    <i className="fi fi-br-lungs"></i>
                    <h4>호흡기</h4>
                  </div>
                </div>
              </label>
              <label htmlFor="radio-card-12" className="radio-card">
                <input
                  type="radio"
                  name="abnormal"
                  id="radio-card-12"
                  value="no health problems"
                  checked={formData.abnormal === "no health problems"}
                  onChange={handleChange}
                />
                <div className="card-content-wrapper">
                  <span className="check-icon"></span>
                  <div className="card-content">
                    <i className="fi fi-br-grin"></i>
                    <h4>없음</h4>
                  </div>
                </div>
              </label>
            </div>
            <hr />
          </AppBar>
          <SoftBox p={3} textAlign="center">
            <p>
              You have selected: {formData.gender === "male" ? "남자" : "여자"}{" "}
              and {formData.age === "young" ? "35세 이하" : "36세 이상"}
            </p>
          </SoftBox>
        </SoftBox>
      </Card>
    </BasicLayout>
  );
}

export default Survey;
