mapper.select:
SELECT '+' AS type, points_added AS points, added_reason AS reason, added_at AS time FROM point_add WHERE user_id = #{userId}
UNION ALL
SELECT '-' AS type, points_subtracted AS points, subtracted_reason AS reason, subtracted_at AS time FROM point_subtract WHERE user_id = #{userId}
ORDER BY time DESC;

mapper.update:
UPDATE point_aggr
SET total_points = total_points + #{pointsAdjusted},
current_points = current_points + #{pointsAdjusted}
WHERE user_id = #{userId};

UPDATE point_aggr
SET current_points = current_points + #{pointsAdjusted}
WHERE user_id = #{userId};
