import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';

const BoardUpdate = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const {num} = useParams();

    const [inputs, setInputs] = useState({
        subject : '',
        content : '',
        filename : null,
    });

    const {subject, content, filename} = inputs;

    const boardDetail = useSelector((state) => state.board.boardDetail);
    const pv = useSelector((state) => state.board.pv);

    useEffect(() => {
        setInputs((prev) => {
            return {...prev, ...boardDetail};
        });
    }, []);

    const handleValueChange = (e) => {
        e.preventDefault();
        setInputs((prev) => {
            return {...prev, [e.target.name] : e.target.value}
        });
    }

    const handleFileChange = (e) => {
        e.preventDefault();
        setInputs({...inputs, [e.target.name] : e.target.files[0]});
    }

    // 취소버튼
    const handleReset = (e) => {
        e.preventDefault();
        setInputs({...inputs, ...boardDetail});
    }

    // 뒤로버튼
    

    return (
        <>
        <form action=''>
        <table className='table table-striped' style={{ marginTop: 20 }}>
            <tbody>
                <tr>
                <th width='20%'>글쓴이</th> <td>null</td>
            </tr>

            <tr>
                <th width='20%'>등록일</th> 
                <td>{boardDetail.reg_date}</td>
            </tr>

            <tr>
                <th>제목</th> 
                <td calSpan='3'>
                    <input 
                    type='text' 
                    name='subject' 
                    id='subject'  
                    value={subject} 
                    onChange={handleValueChange}/>
                </td>
            </tr>

            <tr>
                <th>내용</th>
                <td calSpan='3' style={{ whiteSpace: 'pre-line' }}>
                <textarea 
                name='content' 
                id='content' 
                cols='40' 
                rows='13'
                value={content}
                onChange={handleValueChange}>
                </textarea>
                </td>
            </tr>

            <tr>
                <th>첨부파일</th>
                <td calspan='3'>
                    <input 
                    type='file' 
                    name='filename'
                    id='filepath'
                    onChange={handleFileChange}
                    />
                <span>
                    {boardDetail.upload &&
                        boardDetail.upload.substring(
                        boardDetail.upload.indexOf("_") + 1)}
                </span>
                </td>
            </tr>
            </tbody>
        </table>

        <button className='btn btn-primary'>
            수정
        </button>
        <button className='btn btn-primary'
        onClick={handleReset}>
            취소
        </button>
        <button className='btn btn-primary'>
            뒤로
        </button>
        </form>
        </>
    );
};

export default BoardUpdate;
