import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { boardActions } from '../../toolkit/actions/board_action';

// http://localhost:3000/board/list/1
// http://localhost:3000/board/list/1/:currentPage
//  const {currentPage} = useParams();
const BoardList = () => {
    const {currentPage} = useParams();
    const dispatch = useDispatch();

    useEffect(() => {
        boardActions.getBoardList(currentPage);
    });

    const boardList = useSelector( (state) = store.board,boardList);
    
    return (
        <div>
            <h3 className="text-center">게시판 목록</h3>
            <table className="table table-striped" style={{ marginTop: 20 }}>
                <colgroup>
                <col width="8%" />
                <col width="*" />
                <col width="12%" />
                <col width="12%" />
                </colgroup>

                <thead>
                <tr>
                    <th>번호</th>
                    <th>제목</th>
                    <th>작성자</th>
                    <th>조회수</th>
                </tr>
                </thead>
            </table>
        </div>
    );
};

export default BoardList;
