import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';

const BoardUpdate = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const {num} = useParams();

    const [inputs, setInputs] = useState({
        subject : '',
        content : '',
        filename : null,
    });

    const {subject, content, filename} = inputs;

    const boardDetail = useSelector((state) => state.board.boardDetail);
    const pv = useSelector((state) => state.board.pv);

    useEffect(() => {
        setInputs((prev) => {
            return {...prev, ...boardDetail};
        });
    }, []);


    return (
        <>
        <form action=''>
        <table className='table table-striped' style={{ marginTop: 20 }}>
            <tbody>
                <tr>
                <th width='20%'>글쓴이</th> <td>null</td>
            </tr>
            <tr>
                <th width='20%'>등록일</th> 
                <td>{boardDetail.reg_date}</td>
            </tr>
            <tr>
                <th>제목</th> 
                <td calSpan='3'>
                    <input 
                    type='text' 
                    name='subject' 
                    id='subject' 
                    placeholder={boardDetail.subject} 
                    value={subject} />
                </td>
            </tr>
            <tr>
                <th>메일</th> <td calSpan='3'>{boardDetail.memberEmail}</td>
            </tr>
            <tr>
                <th>내용</th>
                <td calSpan='3' style={{ whiteSpace: 'pre-line' }}>
                {boardDetail.content}
                </td>
            </tr>
            <tr>
                <th>첨부파일</th>
                <td calspan='3'>
                <button onClick={handleDownload}> 
                    {boardDetail.upload &&
                        boardDetail.upload.substring(
                        boardDetail.upload.indexOf("-") + 1)}
                </button>
                </td>
            </tr>
            </tbody>
        </table>
        </form>
        </>
    );
};

export default BoardUpdate;
