import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';
import { boardActions } from '../../toolkit/actions/board_action';

const BoardView = () => {
    const {num} = useParams();
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const boardDetail = useSelector((state) => state.board.boardDetail);
    const pv = useSelector((state) => state.board.pv);

    useEffect(() => {
        dispatch(boardActions.getBoardDetail(num));
    },[]);

    return (
        <>
        <table className='table table-striped' style={{ marginTop: 20 }}>
            <tbody>
                <tr>
                    <th width = '20%'> 글쓴이</th>
                    <td>null</td>
                    </tr>
                <tr>
                    <th width = '20%'> 조회수</th>
                    <td>{boardDetail.readcount}</td>
                </tr>
                <tr>
                    <th>제목</th>
                    <td colSpan='3'>{boardDetail.subject}</td>
                </tr>
                <tr>
                    <th>메일</th>
                    <td colSpan='3'>
                        {boardDetail.memberEmail}
                    </td>
                </tr>
                <tr>
                    <th>내용</th>
                    <td colSpan='3' style={{whiteSpace : 'pre-line'}}>
                        {boardDetail.content}
                    </td>
                </tr>
                <tr>
                    <th>첨부파일</th>
                    <td colSpan='3'>
                        <button>
                            {boardDetail.upload && boardDetail.upload.subString(boardDetail.upload.indexOf("_") + 1
                            )}
                        </button>
                    </td>
                </tr>
            </tbody>
        </table>
        </>
    );
};

export default BoardView;
