import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { boardActions } from '../../toolkit/actions/board_action';

const BoardWrite = () => {

    const dispatch = useDispatch();
    const navigator = useNavigate();
    const {num} = useParams();

    const [inputs, setInputs] = useState({
        subject : "",
        content : "",
        filename : null,
    });

    const {subject, content, filename} = inputs;
    const boardDetail = useSelector((state) => state.board.boardDetail);

    const handleValueChange = (e) => {
        // let nextState = {};
        // nextState[e.target.name] = e.target.value;
        // setInputs({...inputs, ...nextState});
        setInputs((prev) => {
            return {...prev, [e.target.name]: e.target.value};
        });
    }

    const hadleFileChange = (e) => {
        e.preventDefault();
        setInputs((prev) => {
            return{...prev, [e.target.name]:e.target.files[0]}
        })
    }

    const onSubmit = async(e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append("subject", subject);
        formData.append("content", content);

        if(filename != null){
            formData.append("filename", filename);
        } 

        // 답변글이면
        if(num !== undefined){
            formData.append('num', boardDetail)
        }

        const config = {
            headers : {
                'Content-Type' : 'multipart/form-data'
            },
        }

        await dispatch(boardActions.getBoardWrite(formData, config));

        setInputs({
            subject : '',
            content : '',
            filename : null
        });

        navigator(`/board/list/1`);
    }

    return (
        <>
            <form onSubmit={onSubmit}>
                <table>
                    <tbody>
                        <tr>
                            <th>글쓴이</th>
                            <td><input type='text' readOnly />
                            </td>
                        </tr>
                        <tr>
                            <th width = '20%' align='center'>제목</th>
                            <td>
                                <input type='text' name='subject' size='40'  
                                placeholder={num !== undefined ? "답변" : null}
                                onChange={handleValueChange}/>
                            </td>
                        </tr>
                        <tr>
                             <th width = '20%' align='center'>내용</th>
                             <td>
                                <textarea name='content' cols='40' rows='13'
                                onChange={handleValueChange}></textarea>
                             </td>
                        </tr>
                        <tr>
                            <th width = '20%' align='center'>첨부파일</th>
                            <td>
                                <input 
                                type='file'
                                name='filename' 
                                id='filepath' onChange={hadleFileChange}/>
                            </td>
                        </tr>
                    </tbody>
                </table>

                <Link className='btn btn-primary' to={`/board/list/1`}>
                    리스트
                </Link>
                <input type='submit' className='btn btn-primary' value="등록" />
            </form>
        </>
    );
};

export default BoardWrite;
