import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

const PageNavigation = ({getBoardList}) => {
    const pv = useSelector((state) => state.board.pv ? state.board.pv : {currentPage : 1});

    const pageNumbers = [];

    for(let i =pv.startPage; i <= pv.endPage; i++){
        pageNumbers.push(i);
    }

    return (
        <nav aria-label='...'>
            <ul className='pagination'>
                {/* 이전 */}
                <li className={pv.startPage <= 1 ? 'page-item idsabled' : 'page-item'}>
                    <span className='page-link' onClick={() => getBoardList(pv.startPage - pv.blockPage)}> &laquo; </span>
                </li>
                {/* 페이지 번호 */}
                {pageNumbers && pageNumbers.map((pnum, idx) => {
                    return <li key={pnum}>
                        <span 
                        onClick={() => getBoardList(pnum)} 
                        className={pv.currentPage === pnum 
                        ? "page-item active page-link" 
                        : "page-link"
                        }>
                        {pnum}
                        </span>
                    </li>
                })}
            </ul>
        </nav>
    );
};

export default PageNavigation;
