import React from 'react';
import { useSelector } from 'react-redux';

const PageNavigation = () => {
    const pv = useSelector((state) => state.board.pv ? state.board.pv : {currentPage : 1});

    const pageNumbers = [];

    for(let i =pv.startPage; i <= pv.endPage; i++){
        pageNumbers.push(i);
    }

    return (
        <nav aria-label='...'>
            <ul className='pagination'>

                {pageNumbers && pageNumbers.map((pnum, idx) => {
                    return <li key={pnum}>
                        <span className={pv.currentPage === pnum ? 'page-item active' : null}>
                        <Link to = {`/board/lsit/${pnum}`} className="page-link">
                        {pnum}
                        </Link> 
                        </span>
                    </li>
                })}
            </ul>
        </nav>
    );
};

export default PageNavigation;
